import { StyleSheet, Text, View, FlatList } from "react-native";
import { movies } from "./src/data/movies.js";
import { series } from "./src/data/series.js";

// COMPONENTS
import Header from "./src/components/header/index.js";
import SerchBar from "./src/components/SerchBar/index.js";
import CardItem from "./src/components/cardItem/index.js";
import Title from "./src/components/Title/index.js";
import BannerFilms from "./src/components/BannerFilms/index.js";

export default function App() {
  return (
    <View style={styles.container}>
      <Header />
      <SerchBar />
      <BannerFilms />

      <Title>Filmes</Title>
      <FlatList
        style={{ width: "100%", margin: 15, paddingRight: 10, paddingLeft: 20 }}
        horizontal
        showsHorizontalScrollIndicator={false}
        data={movies}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <CardItem nome={item.nome} nota={item.nota} imagem={item.imagem} />
        )}
      />

      <Title>SÃ©ries</Title>
      <FlatList
        style={{ width: "100%", margin: 15, paddingRight: 10, paddingLeft: 20 }}
        horizontal
        showsHorizontalScrollIndicator={false}
        data={series}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <CardItem nome={item.nome} nota={item.nota} imagem={item.imagem} />
        )}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#141A29",
    alignItems: "center",
  },
});
