import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  containerFilmes: {
    paddingTop: 10,
    paddingBottom: 16,
    paddingRight: 16,
    width: 140,
  },

  tituloFilmes: {
    color: 'white',
    fontSize: 12,
    paddingTop: 8,
  },

  notaFilme: {
    fontSize: 10,
    color: 'white',
    paddingLeft: 4,
  },
  imagemFilmes: {
    width: '100%',
    height: 170,
    borderRadius: 8,
  },
});

export default styles;
